import { Pipe, PipeTransform } from '@angular/core';
import { AbstractControl } from '@angular/forms';

@Pipe({
    name: 'checkEventsControls',
    pure: false,
})
export class CheckEventsControlsPipe implements PipeTransform {
    transform(eventsControls: AbstractControl[], ...args: any[]): any {
        const filteredEventsControls = eventsControls.filter(
            ({ value: { changeStatus } }) => changeStatus !== 'DELETED',
        );

        const hasOnlyOneEvent = filteredEventsControls.length === 1;

        return hasOnlyOneEvent;
    }
}

import { Pipe, PipeTransform } from '@angular/core';
import { PricePeriod } from 'app/@core/models/product.model';

@Pipe({
    name: 'intervalLabel',
})
export class ConvertIntervalLabel implements PipeTransform {
    transform(value: PricePeriod, args?: any): string {
        if (value.intervalCount === 1) {
            if (value.interval === 'day') {
                return 'daily';
            }
            return `${value.interval}ly`; // yearly, monthly, weekly
        }

        return `every ${value.intervalCount} ${value.interval}s`;
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'convertTime',
})
export class ConvertTimePipe implements PipeTransform {
    transform(value: any, args?: any): any {
        const hour24h = +value.split(':').shift();
        const hour12h = ((hour24h + 11) % 12) + 1;
        const addZero = hour12h < 10 ? '0' : '';
        return `${addZero}${hour12h}:00`;
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'filterEnrollmentGroup',
})
export class FilterEnrollmentGroupPipe implements PipeTransform {
    transform(groups: any, ...args: any): any {
        const [program, level] = args;

        if (!program && !level) {
            return groups;
        }

        let programCode: string;
        let levelCompleteCode: string;
        if (program) {
            programCode = program.code;
        }

        if (level) {
            levelCompleteCode = level.code;
        }

        const [levelModule, levelType, levelCode] = levelCompleteCode?.split('-') || [];

        const filteredGroups = groups.filter((group) => {
            const [module, type, level] = group.level.code.split('-');

            const showGroup = levelCode ? module === programCode && level === levelCode : module === programCode;

            return showGroup;
        });

        return filteredGroups;
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'filterLessons',
})
export class FilterLessonsPipe implements PipeTransform {
    transform(value: any, ...args: any[]): any {
        const lessons = value;
        const [selectedFilters] = args;

        if (!selectedFilters || selectedFilters.length === 0) {
            return lessons;
        }

        const filteredLessons = lessons.filter((lesson) => selectedFilters.includes(lesson.category.toLowerCase()));

        return filteredLessons;
    }
}

