import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'formatEventTitle',
})
export class FormatEventTitlePipe implements PipeTransform {
    transform(value: any, args?: any): any {
        return value.split(/(\([0-9]:)/).shift();
    }
}

import { DatePipe } from '@angular/common';
import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'formatNextClassDate',
})
export class FormatNextClassDatePipe implements PipeTransform {
    constructor(private datePipe: DatePipe) {}

    transform(value: any, args?: any): any {
        const date = new Date(+value);

        const dayName = this.datePipe.transform(date, 'EEE');
        const dateString = this.datePipe.transform(date, 'dd/MM/yy');
        const hour = this.datePipe.transform(date, 'hh:mm aa');

        const span = ({ text, className, upperCase }: { text: string; className: string; upperCase?: boolean }) =>
            `<span class="${className}" ${upperCase ? 'style="text-transform: uppercase"' : ''}>${text}</span>`;

        return `
          ${span({ text: dayName, className: 'mat-button-text mat-button-text--size-12', upperCase: true })}
          ${span({ text: dateString, className: 'mat-overline' })}
          ${span({ text: hour, className: 'mat-button-text mat-button-text--size-12' })}
          `;
    }
}

import { DatePipe } from '@angular/common';
import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'invoiceDate',
})
export class InvoiceDatePipe implements PipeTransform {
    constructor(private datePipe: DatePipe) {}

    transform(value: any, args?: any): any {
        if (!value) {
            return;
        }

        const dateSplit = value.split(':')[0] as string;
        const date = dateSplit.substring(0, dateSplit.length - 2);
        const formattedDate = this.datePipe.transform(date);

        return formattedDate;
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'invoiceDays',
})
export class InvoiceDaysPipe implements PipeTransform {
    transform(value: any, args?: any): any {
        const now = new Date();
        const days = new Date(now.getFullYear(), now.getMonth() + 1, 0).getDate();

        return Math.round(value * days);
    }
}

import { Pipe, PipeTransform } from '@angular/core';
import { ClassRecord, ClassRecordType } from 'app/@core/models/class-record.model';

@Pipe({
    name: 'isLastClassLessonDone',
})
export class IsLastClassLessonDonePipe implements PipeTransform {
    transform(value: any, args?: any): any {
        const lesson = value;
        let tracker = lesson?.classRecords || lesson?.studyGroupLessonRecord;
        if (!tracker || tracker.length === 0) {
            return undefined;
        }

        let lastClass: ClassRecordType;
        tracker.forEach((classLectured: ClassRecordType) => {
            const currentStartedDate = new Date(parseInt(classLectured.startedAt, 10)).getDate();
            if (!lastClass || parseInt(classLectured.startedAt, 10) > parseInt(lastClass.startedAt, 10)) {
                lastClass = classLectured;
            }
        });

        return lastClass?.status === 'LESSON_DONE';
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'minutesToHours',
})
export class MinutesToHoursPipe implements PipeTransform {
    transform(value: any, args?: any): any {
        const hours = Math.floor(value / 60);
        const minutes = value % 60;

        const hourLabel = hours > 1 ? 'hours' : 'hour';
        const minuteLabel = minutes > 1 ? 'minutes' : 'minute';

        return `${hours === 0 ? '' : `${hours} ${hourLabel}`} ${minutes === 0 ? '' : `${minutes} ${minuteLabel}`}`;
    }
}

import { Pipe, PipeTransform } from '@angular/core';
import { ClassRecordType } from 'app/@core/models/class-record.model';

@Pipe({
    name: 'parseCompletedLessons',
})
export class ParseCompletedLessonsPipe implements PipeTransform {
    transform(classRecords: ClassRecordType[], args?: any): any {
        if (!classRecords || classRecords.length === 0) {
            return 0;
        }

        const lessonsDoneLength = classRecords.filter((classRecord) => classRecord.status === 'LESSON_DONE').length;

        return lessonsDoneLength;
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'programLevelRange',
})
export class ProgramLevelRangePipe implements PipeTransform {
    transform(modules: any, args?: any): any {
        const levels = [].concat(...modules.map((module) => module.levels));
        const firstLevel = levels[0].code.split('-').pop();
        const lastLevel = levels[levels.length - 1].code.split('-').pop();

        return `${firstLevel} - ${lastLevel}`;
    }
}

import { Pipe, PipeTransform } from '@angular/core';
import { DomSanitizer, SafeHtml, SafeStyle, SafeScript, SafeUrl, SafeResourceUrl } from '@angular/platform-browser';

@Pipe({
    name: 'safe',
})
export class SafePipe implements PipeTransform {
    constructor(protected sanitizer: DomSanitizer) {}

    public transform(value: any, type: string): SafeHtml | SafeStyle | SafeScript | SafeUrl | SafeResourceUrl {
        switch (type) {
            case 'html':
                return this.sanitizer.bypassSecurityTrustHtml(value);
            case 'style':
                return this.sanitizer.bypassSecurityTrustStyle(value);
            case 'script':
                return this.sanitizer.bypassSecurityTrustScript(value);
            case 'url':
                return this.sanitizer.bypassSecurityTrustUrl(value);
            case 'resourceUrl':
                return this.sanitizer.bypassSecurityTrustResourceUrl(value);
            default:
                throw new Error(`Invalid safe type specified: ${type}`);
        }
    }
}

import { DatePipe } from '@angular/common';
import { Pipe, PipeTransform } from '@angular/core';
import { MediaObserver } from '@angular/flex-layout';

@Pipe({
    name: 'utcToDate',
})
export class UTCToDatePipe implements PipeTransform {
    constructor(private mediaObserver: MediaObserver, private datePipe: DatePipe) {}

    transform(value: any, args?: any): any {
        if (!value) {
            return '    ';
        }

        const date = new Date(+value);

        const isMobile = this.mediaObserver.isActive('xs');

        let dateLabel = this.datePipe.transform(date, 'dd/MM/yyyy hh:mm aa');
        if (isMobile) {
            dateLabel = dateLabel.split(/(?<=^\S+)\s/).join('<br/>');
        }

        return dateLabel;
    }
}

