            .watchQuery<IStudentDashboardQuery>({
                query: gql`
                    query StudentDashboardQuery($filters: DashboardFilters) {
                        studentDashboard(filters: $filters) {
                            id
                            nextEvent {
                                id
                                title
                                description
                                startDateTime
                                enrollment {
                                    ...enrollmentDetail
                                }
                                studyGroup {
                                    ...studyGroupSummary
                                }
                            }
                            subscriptions {
                                id
                                renewalDate
                            }
                        }
                    }
                    ${ENROLLMENT_DETAIL}
                    ${STUDY_GROUP_SUMMARY}
                `,
                variables: {
                    filters,
                },
            })
            .valueChanges.pipe(
                map((result: ApolloQueryResult<IStudentDashboardQuery>) => result.data.studentDashboard),
            );
    }
}

