}

import { Injectable } from '@angular/core';
import { NavigationEnd, Router } from '@angular/router';

import { ApolloQueryResult } from '@apollo/client/core';
import { Apollo, gql } from 'apollo-angular';

interface ILiveChatTokenResult {
    liveChatToken: string;
}

@Injectable({
    providedIn: 'root',
})
export class LiveChatService {
    private zE: any;
    private currentRoute: string;
    private liveChatToken: string;

    /**
     * Live Chat Settings
     *  - only chat and contact form enabled
     *  - set authentication based on live chat token (from server)
     *  - offset live chat icon (very important om mobile)
     */
    private zESettings = {
        webWidget: {
            chat: { supress: false },
            contactForm: { suppress: false },
            helpCenter: { supress: true },
            talk: { supress: true },
            authenticate: {
                chat: {
                    jwtFn: (callback: Function) => {
                        if (this.liveChatToken) {
                            callback(this.liveChatToken);
                        }
                    },
                },
            },
            offset: {
                horizontal: '20px',
                vertical: '20px',
                mobile: {
                    horizontal: '0px',
                    vertical: '60px', // TODO: @Rodrigo check if we can calculate offset
                },
            },
        },
    };

    constructor(private router: Router, private apollo: Apollo) {
        this.zE = window['zE'];
        this.watchRoute();
    }

    private enableChat(): boolean {
        return (
            this.zE !== undefined && this.currentRoute !== undefined && ['/dashboard', '/'].includes(this.currentRoute)
        );
    }

    /**
     * Watch route and toggle live chat depending on route
     */
    private watchRoute(): void {
        this.router.events.subscribe({
            next: (event) => {
                if (event instanceof NavigationEnd) {
                    const { url } = event as NavigationEnd;

                    if (this.currentRoute !== url) {
                        this.currentRoute = url;
                        if (this.currentRoute) {
                            this.updateSettings();
                        }
                    }
                }
            },
        });
    }

    /**
     * This function is executed from user service when jwt is set (user logged in)
     */
    getLiveChatToken(): void {
        this.apollo
            .query({
                query: gql`
                    {
                        liveChatToken
                    }
                `,
            })
            .subscribe((result: ApolloQueryResult<ILiveChatTokenResult>) => {
                this.liveChatToken = result.data.liveChatToken;
                this.updateSettings();
            });
    }

    /**
     * update Live Chat settings (check zESettings)
     * TODO: error when hard reloading page after toggling chat. Steps to reproduce:
     *  - login and stay on dashboard; hit refresh. No error occurrs.
     *  - Hard reset the page. Error occurs.
     */
    private updateSettings(): void {
        this.zE = window['zE'];
        this.zE('webWidget', 'updateSettings', this.zESettings);

        if (typeof this.zE.show !== 'function') {
            return;
        }

        if (this.enableChat()) {
            this.zE.show();
        } else if (this.currentRoute) {
            this.zE.hide();
        }
    }
}

import { Apollo, gql } from 'apollo-angular';
import { ApolloQueryResult } from '@apollo/client/core';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { BehaviorSubject, Observable, of } from 'rxjs';
import { map } from 'rxjs/operators';

import { environment } from 'environments/environment';
import { UserAuthDetails, UserProfile } from 'app/@core/models/user-profile.model';
import { UserSessionService } from 'app/@core/services/user-session.service';
import { USER_PROFILE } from '../graphql/user-profile.graphql';
import { CreatePasswordStudentInfo } from '../models/invitation.model';
import { LiveChatService } from './live-chat.service';

interface ILoginResult {
    token: string;
}

interface IUserResult {
    myProfile: UserProfile;
}

interface IUpdateMyProfileResult {
    updateMyProfile: UserProfile;
}

interface IChangePassword {
    user: UserProfile;
}

interface INewUser {
    email: string;
    password: string;
    firstName: string;
    lastName: string;
    cpf: string;
    postalCode: string;
    street: string;
    streetNumber: string;
    streetComplement: string;
    district: string;
    city: string;
    state: string;
    addressType?: string;
    countryISO?: string;
    category?: string;
    earlyAccessCode?: string;
}

@Injectable({ providedIn: 'root' })
export class UserService {
    private loggedUserSubject: BehaviorSubject<UserAuthDetails>;
    public loggedUser: Observable<UserAuthDetails>;

    private studentInfoSubject = new BehaviorSubject<CreatePasswordStudentInfo | null>(null);
    studentInfo$ = this.studentInfoSubject.asObservable();

    constructor(
        private http: HttpClient,
        private apollo: Apollo,
        private _userSessionService: UserSessionService,
        private liveChatService: LiveChatService,
    ) {
        this.loggedUserSubject = new BehaviorSubject<UserAuthDetails>(null);
        this.loggedUser = this.loggedUserSubject.asObservable();

        this.setUserFromAuthToken().subscribe();
    }

    setUserFromAuthToken(): Observable<UserAuthDetails> {
        const jwt = this._userSessionService.getAuthToken();
        if (!jwt) {
            return of(undefined);
        }

        this.liveChatService.getLiveChatToken();
        const jwtPayload = JSON.parse(atob(jwt.split('.')[1])) as UserAuthDetails;
        if (!jwtPayload.roles) {
            jwtPayload.roles = [];
        }

        this.loggedUserSubject.next(jwtPayload);

        return of(jwtPayload);
    }

    getUserProfile(): Observable<UserProfile> {
        if (!this._userSessionService.getAuthToken()) {
            return;
        }

        return this.apollo
            .query({
                query: gql`
                    {
                        myProfile {
                            ...userProfile
                        }
                    }
                    ${USER_PROFILE}
                `,
            })
            .pipe(map((result: ApolloQueryResult<IUserResult>) => result.data.myProfile));
    }

    updateMyProfile(userProfile: UserProfile): Observable<UserProfile> {
        return this.apollo
            .mutate<IUpdateMyProfileResult>({
                variables: {
                    userProfile,
                },
                mutation: gql`
                    mutation updateMyProfile($userProfile: UserInput) {
                        updateMyProfile(userProfile: $userProfile) {
                            ...userProfile
                        }
                    }
                    ${USER_PROFILE}
                `,
            })
            .pipe(
                map((result: ApolloQueryResult<IUpdateMyProfileResult>) => {
                    const updatedUser = result.data.updateMyProfile;
                    return updatedUser;
                }),
            );
    }

    changePassword(changePasswordInput: {
        userEmail: string;
        oldPassword: string;
        newPassword: string;
    }): Observable<any> {
        return this.apollo
            .mutate<IChangePassword>({
                variables: {
                    changePasswordInput,
                },
                mutation: gql`
                    mutation changeMyPassword($changePasswordInput: ChangePasswordInput) {
                        changeMyPassword(changePasswordInput: $changePasswordInput) {
                            id
                        }
                    }
                `,
            })
            .pipe(
                map((result: ApolloQueryResult<IChangePassword>) => {
                    const updatedUser = result.data.user;
                    return updatedUser;
                }),
            );
    }

    register(newUser: INewUser): Observable<{ token: string }> {
        return this.http.post<{ token: string }>(`${environment.apiUrl}/auth/register`, newUser);
    }

    login(email: string, password: string): Observable<{ token: string }> {
        return this.http.post<any>(
            `${environment.apiUrl}/auth/login`,
            { email, password },
            {
                headers: {
                    'Content-Type': 'application/json',
                },
                responseType: 'json',
            },
        );
    }

    login2(email: string, password: string): Observable<UserProfile> {
        return this.http.post<any>(`${environment.apiUrl}/auth/login`, { email, password });
    }

    loginSuccess(result: ILoginResult) {
        this._userSessionService.setAuthToken(result.token);
        return this.setUserFromAuthToken();
    }

    logout(): void {
        this.loggedUserSubject.next(null);
        this._userSessionService.removeAuthToken();
    }

    forgotPassword(email: string): Observable<any> {
        return this.http.post<any>(
            `${environment.apiUrl}/auth/forgot-password`,
            { email },
            {
                headers: {
                    'Content-Type': 'application/json',
                },
                responseType: 'json',
            },
        );
    }

    resetPassword(resetPasswordToken: string, newPassword: string): Observable<any> {
        return this.http.post<any>(
            `${environment.apiUrl}/auth/reset-password`,
            { resetPasswordToken, newPassword },
            {
                headers: {
                    'Content-Type': 'application/json',
                },
                responseType: 'json',
            },
        );
    }

    validateEmail(email: string): Observable<any> {
        return this.http.post<any>(
            `${environment.apiUrl}/auth/email-validation`,
            { primaryEmail: email },
            {
                headers: {
                    'Content-Type': 'application/json',
                },
                responseType: 'json',
            },
        );
    }

    setStudentInfoForCreatePassword(studentInfo: CreatePasswordStudentInfo) {
        this.studentInfoSubject.next(studentInfo);
    }

    getStudentInfo() {
        return this.studentInfoSubject.getValue();
    }

    hasRole(role: string) {
        const { roles } = this.loggedUserSubject.getValue();
        return roles.includes(role);
    }
}

