            .pipe(
                map((result: ApolloQueryResult<ICreateEnrollmentMutation>) => {
                    return result.data.createEnrollment;
                }),
            );
    }

    getEnrollment(enrollmentId: string): Observable<Enrollment> {
        this.getEnrollmentQuery$ = this.apollo.watchQuery({
