import { HttpEvent, HttpHandler, HttpInterceptor, HttpRequest } from '@angular/common/http';
import { Injectable, Injector } from '@angular/core';

import { Observable, throwError } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { UserSessionService } from '../../services/user-session.service';

@Injectable()
export class AuthErrorHandler implements HttpInterceptor {
    constructor(private userSessionService: UserSessionService, private injector: Injector) {}

    intercept(request: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
        return next.handle(request).pipe(
            catchError((err) => {
                if (err.status === 401) {
                    this.userSessionService.removeAuthToken();
                }

                const error = err.error.message || err.statusText;
                return throwError(error);
            }),
        );
    }
}

import { Injectable } from '@angular/core';
import { HttpRequest, HttpHandler, HttpEvent, HttpInterceptor, HttpResponse } from '@angular/common/http';
import { Observable } from 'rxjs';
import { catchError, map } from 'rxjs/operators';
import { ProgressBarService } from 'app/@core/components/progress-bar/progress-bar.service';
import { throwError } from 'rxjs';

/**
 * This class is for intercepting http requests. When a request starts, we set the loadingSub property
 * in the LoadingService to true. Once the request completes and we have a response, set the loadingSub
 * property to false. If an error occurs while servicing the request, set the loadingSub property to false.
 *
 * @class {HttpRequestInterceptor}
 */
@Injectable()
export class HttpRequestInterceptor implements HttpInterceptor {
    constructor(private progressBarService: ProgressBarService) {}

    intercept(request: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
        this.progressBarService.setMode('indeterminate');
        this.progressBarService.show();
        return next.handle(request).pipe(
            catchError((err) => {
                this.progressBarService.hide();
                return throwError(err);
            }),
            map<HttpEvent<any>, any>((evt: HttpEvent<any>) => {
                if (evt instanceof HttpResponse) {
                    this.progressBarService.hide();
                }
                return evt;
            }),
        );
    }
}

import { Injectable } from '@angular/core';
import { HttpRequest, HttpHandler, HttpEvent, HttpInterceptor, HttpResponse } from '@angular/common/http';
import { Observable } from 'rxjs';

import { UserSessionService } from '../../services/user-session.service';
import { tap } from 'rxjs/operators';

@Injectable()
export class JwtInterceptor implements HttpInterceptor {
    constructor(private userSessionService: UserSessionService) {}

    intercept(request: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
        const token = this.userSessionService.getAuthToken();
        const skipIntercept = request.headers.has('skip');

        if (skipIntercept) {
            request = request.clone({
                headers: request.headers.delete('skip'),
            });

            return next.handle(request);
        }

        if (token) {
            request = request.clone({
                setHeaders: {
                    Authorization: `Bearer ${token}`,
                },
            });
        }

        return next.handle(request).pipe(
            tap((event) => {
                if (event instanceof HttpResponse) {
                    const response = event as HttpResponse<any>;

                    const token = response.headers.get('Token');
                    if (token) {
                        this.userSessionService.setAuthToken(token);
                    }
                }
            }),
        );
    }
}

