        links: [
            { path: 'month', icon: 'event_note' },
            { path: 'availability', icon: 'group' },
        ],
    };

    private destroy$ = new Subject<void>();

    constructor(private mediaObserver: MediaObserver, private router: Router) {
        this.router.events
            .pipe(
                takeUntil(this.destroy$),
                filter((evt) => evt instanceof NavigationEnd),
            )
            .subscribe({
                next: ({ url }: NavigationEnd) => {
                    if (url === '/calendar' && this.isMobile) {
                        this.router.navigate(['/calendar/month']);
                    }
                },
            });
    }

    ngOnDestroy() {
        this.destroy$.next();
        this.destroy$.complete();
    }

    get isMobile() {
        return this.mediaObserver.isActive('xs');
    }
}

