        this.getGroupById();
    }

    getGroupById(): void {
        const groupId = this.route.snapshot.params.groupId;

        this.group$ = this.classroomService.getGroupById(groupId).pipe(
            map((group) => {
                const activeEnrollments = group.enrollments.filter(
                    (enrollment) => enrollment.status === ENROLLMENT_STATUS.ACTIVE,
                );

                return { ...group, enrollments: activeEnrollments };
            }),
        );
    }

    /**
     * add new member using invitation
     */
    addNewMember(group: StudyGroup): void {
        const dialogRef = this.matDialog.open(InvitationDialogComponent, {
            panelClass: 'invitation-dialog--material',
            autoFocus: false,
            disableClose: true,
            data: {
                type: 'student',
                selectedGroup: group,
                tabs: [
                    {
                        title: 'info',
                        active: false,
                        completed: false,
                        icon: 'account_circle',
                    },
                    {
                        title: 'course',
                        active: false,
                        completed: false,
                        icon: 'school',
                    },
                    {
                        title: 'schedule',
                        active: false,
                        completed: false,
                        icon: 'date_range',
                    },
                ] as ITab[],
            },
        });

        dialogRef.afterClosed().subscribe((response: any) => {
            if (!response) {
                return;
            }

            const { studentForm, courseForm, eventsForm } = response;

            let browserTimeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;
            if (!browserTimeZone) {
                // default to SP if not found
                browserTimeZone = 'America/Sao_Paulo';
            }

            const enrollmentInvitation: CreateEnrollmentInput = {
                invitedStudent: {
                    email: studentForm.studentInfo.email,
                    firstName: studentForm.studentInfo.firstName,
                    surname: studentForm.studentInfo.lastName,
                },
                enrollmentInput: {},
            };

            if (studentForm?.selectedGroup) {
                enrollmentInvitation.enrollmentInput.studyGroupId = studentForm?.selectedGroup?.id;
            } else {
                enrollmentInvitation.enrollmentInput.oneOnOne = {
                    levelId: courseForm.level.id,
                    events: eventsForm.events.map((event: Event) => ({
                        title: `${studentForm.studentInfo.firstName} ${
                            studentForm.studentInfo.lastName ? studentForm.studentInfo.lastName : ''
                        } (1 on 1)`,
                        description: courseForm.level.name,
                        sourceType: 'ENROLLMENT',
                        availabilityType: 'BUSY',
                        status: 'TENTATIVE',
                        recurrence: event.recurrence,
                        startDateTime: new Date(parseInt(event.startDateTime, 10)).toISOString(),
                        startTimeZone: browserTimeZone,
                        endDateTime: new Date(parseInt(event.endDateTime, 10)).toISOString(),
                        endTimeZone: browserTimeZone,
                    })),
                };
            }

            this.classroomService
                .createEnrollmentInvitation(enrollmentInvitation)
                .pipe(take(1))
                .subscribe((pendingEnrollment: Enrollment) => {
                    if (pendingEnrollment) {
                        this.classroomService.classroomRefetch();
                    }
                });
        });
    }

    addExistingMembers(group: StudyGroup) {
        let toastRef: MatSnackBarRef<any>;

        this.classroomService
            .getClassroom$()
            .pipe(
                take(1),
                map(({ students }) => {
                    const enrollments = students.reduce((acc, currStudent) => {
                        const enrollments = currStudent.enrollments
                            .filter((enrollment) => {
                                const validEnrollment =
                                    enrollment?.studyGroup?.id !== group.id &&
                                    enrollment.level.code === group.level.code &&
                                    enrollment.status === ENROLLMENT_STATUS.ACTIVE;
                                return validEnrollment;
                            })
                            .map((enrollment) => ({ ...enrollment, selected: false }));

                        return [...acc, ...enrollments];
                    }, []);

                    return enrollments;
                }),
                switchMap((enrollments) => {
                    const dialogRef = this.matDialog.open(SelectMembersDialogComponent, {
                        autoFocus: false,
                        disableClose: true,
                        panelClass: 'dialog-border-radius--padding',
                        data: {
                            enrollments,
                        },
                    });

                    return dialogRef.afterClosed();
                }),
                switchMap((selectedEnrollments: Enrollment[]) => {
                    if (!selectedEnrollments || selectedEnrollments.length === 0) {
                        return EMPTY;
                    }

                    const requests = selectedEnrollments.map(({ id }) =>
                        this.classroomService.addStudentToGroup({ id, studyGroupId: group.id }),
                    );

                    toastRef = this.matSnackBar.open('Adding members...', '', {
                        duration: 0,
                        panelClass: ['snackbar-panel', 'snackbar-panel--one-line'],
                    });

                    return forkJoin(requests);
                }),
            )
            .subscribe({
                next: (response) => {
                    this.matSnackBar.open('All students added successfully', 'Ok', {
                        duration: 0,
                        panelClass: ['snackbar-panel', 'snackbar-panel--one-line'],
                    });
                },
                error: (err) => {
                    this.matSnackBar.open('Error while adding members', 'Ok', {
                        duration: 0,
                        panelClass: ['snackbar-panel', 'snackbar-panel--one-line'],
                    });
                },
                complete: () => {
                    if (toastRef) {
                        toastRef.dismiss();
                    }
                },
            });
    }

    onRemoveGroup(studyGroupId: string): void {
        const dialogRef = this.matDialog.open<ModalConfirmationComponent, ModalConfirmationDataOptions>(
            ModalConfirmationComponent,
            {
                restoreFocus: false,
                disableClose: true,
                data: {
                    title: 'Remove group',
                    message: 'Are you sure you want to remove this group?',
                },
            },
        );

        dialogRef
            .afterClosed()
            .pipe(
                take(1),
                switchMap((confirm: boolean) => {
                    if (!confirm) {
                        return EMPTY;
                    }

                    return this.classroomService.removeStudyGroup({ studyGroupId });
                }),
            )
            .subscribe({
                next: (group: any) => {
                    this.classroomService.getGroupsQuery().refetch();
                    this.router.navigate(['../../'], { relativeTo: this.route });
                },
            });
    }

    onEnrollmentDetail(enrollmentId: string): void {
        this.router.navigate(['classroom', 'enrollments', enrollmentId]);
    }

    onEnrollmentEdit(enrollmentId: string): void {}
}

import { Component, Input, OnDestroy, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { Level } from 'app/@core/models/level.model';
import { Program } from 'app/@core/models/program.model';
import { LMSService } from 'app/@core/services/lms.service';
import { Subject } from 'rxjs';
import { takeUntil } from 'rxjs/operators';

@Component({
    selector: 'app-group-program',
    templateUrl: './group-program.component.html',
    styleUrls: ['./group-program.component.scss'],
})
export class GroupProgramComponent implements OnInit, OnDestroy {
    @Input() groupProgramControl: FormControl;
    @Input() groupLevelControl: FormControl;
    @Input() isEditing: boolean;

    selectedProgramIndex: number;
    localPrograms = [
        { name: 'business', icon: 'work', levels: ['B1', 'B2'] },
        { name: 'extensive', icon: 'backpack', levels: ['A1', 'A2', 'B1', 'B2', 'C1'] },
        { name: 'ielts', icon: 'grading', levels: ['A2', 'B1', 'B2'] },
    ];

    programs: Program[];
    levels: Level[];

    selectedTabIndex = 0;
    levelsA = [{ name: '' }, { name: 'A1' }, { name: 'A2' }, { name: 'B1' }, { name: 'B2' }, { name: 'C1' }];

    private destroy$ = new Subject<void>();

    constructor(private lmsService: LMSService) {}

    ngOnInit() {
        this.getPrograms();
        this.listenGroupProgramControl();
    }

    checkHasSelectedLevel() {
        if (!this.groupLevelControl.value) {
            return;
        }
        const { code } = this.groupLevelControl.value;
        const [programCode, type, levelCode] = code.split('-');
        const selectedProgramIndex = this.programs.findIndex((program) => program.code === programCode);
        this.onSelectProgram(selectedProgramIndex);

        const selectedLevelIndex = this.levels.findIndex((level) => level?.code.split('-').pop() === levelCode);
        this.selectedTabIndex = selectedLevelIndex;
        this.onLevelSelectedIndexChange(selectedLevelIndex);
    }

    ngOnDestroy(): void {
        this.destroy$.next();
        this.destroy$.complete();
    }

    getPrograms() {
        this.lmsService.getPrograms().subscribe({
            next: (programs) => {
                this.programs = programs;

                this.checkHasSelectedLevel();
            },
        });
    }

    listenGroupProgramControl() {
        this.groupProgramControl.valueChanges.pipe(takeUntil(this.destroy$)).subscribe({
            next: (value) => {
                if (!value) {
                    return;
                }
                this.getLevels();
            },
        });
    }

    getLevels(): void {
        const program = this.groupProgramControl.value;
        /* first value it's null to show the correct tabs
           because in css the first one is hidden */
        this.levels = [null].concat(...program.modules.map((module) => module.levels));
    }

    onSelectProgram(index: number): void {
        if (this.selectedProgramIndex === index) {
            return;
        }
        this.selectedProgramIndex = index;
        const selectedProgram = this.programs[this.selectedProgramIndex];
        this.groupProgramControl.setValue(selectedProgram);
        this.selectedTabIndex = 0;
    }

    onLevelSelectedIndexChange(levelIndex: number): void {
        const selectedLevel = this.levels[levelIndex];
        this.groupLevelControl.setValue(selectedLevel);
    }
}

