@use '~@angular/material' as mat;
@mixin group-program-theme($theme) {
    $primary: mat.get-color-from-palette(map-get($theme, primary));
    $primary-50: mat.get-color-from-palette(map-get($theme, primary), 50);

    $accent: mat.get-color-from-palette(map-get($theme, accent));

    .group-program {
        &__program {
            position: relative;
            display: flex;
            flex-direction: column;
            justify-content: flex-end;
            align-items: center;
            border: 1px solid rgba(0, 0, 0, 0.12);
            color: $primary;
            width: 100px;
            height: 100px;
            border-radius: 14px;
            cursor: pointer;
            padding-bottom: 10px;

            &__name {
                text-align: center;
                font-weight: 600;
                text-transform: uppercase;
                font-size: 13px;
                line-height: 16px;
                letter-spacing: 1.25px;
                margin-top: 10px;
            }

            &__selected-icon {
                position: absolute;
                top: 10px;
                right: 10px;
            }

            mat-icon:not(.group-program__program__selected-icon) {
                font-size: 30px;
                width: 30px;
                height: 30px;
            }

            &--active {
                color: $accent;
                border: 1px solid $primary;
                background-color: $primary-50;
            }

            &--disabled {
                cursor: auto;
                color: gray;
                border: 1px solid gray;
                background-color: lightgray;
            }
        }

        &__level-tab-group {
            margin-top: 24px;
            display: flex;
            align-items: center;

            .mat-tab-header {
                border-bottom: none;
            }

            .mat-tab-label {
                width: 66px !important;
                min-width: 66px !important;
            }

            .mat-tab-label-active {
                color: $primary;
                opacity: 1 !important;

                &.mat-tab-disabled {
                    color: lightgray;
                    opacity: 1 !important;
                }
            }

            // disable fist tab to make it possible to show none active tab
            // https://stackoverflow.com/questions/53817845/set-no-tab-as-default-active-tab-on-mat-tab
            .mat-tab-labels > .mat-tab-label:first-child {
                display: none;
            }
        }
    }
}

