@use '~@angular/material' as mat;

.mb-16 {
    margin-bottom: 16px !important;
}

.pulse-animation {
    animation: pulse 1.5s infinite;

    @keyframes pulse {
        0% {
            transform: scale(1);
        }

        50% {
            transform: scale(1.25);
        }

        100% {
            transform: scale(1);
        }
    }
}

@mixin invitation-dialog-theme($theme) {
    $primary: mat.get-color-from-palette(map-get($theme, primary));
    $accent: mat.get-color-from-palette(map-get($theme, accent));

    $accent-200: mat.get-color-from-palette(map-get($theme, accent), 200);

    $background: mat.get-color-from-palette(map-get($theme, background), card);
    $bg-background: mat.get-color-from-palette(map-get($theme, background), background);

    .bg-primary {
        background-color: $primary;
    }

    .header {
        background-color: $primary;

        &__buttons {
            padding: 10px 10px 0 10px;
        }
    }

    .tabs {
        display: flex;
        justify-content: space-evenly;
        align-items: center;
        padding: 20px 0 15px 0;
        position: relative;

        &.normal-curve {
            &::before {
                content: '';
                position: absolute;
                width: 20px;
                height: 20px;
                right: 0;
                bottom: -27px;
                background: radial-gradient(circle at 100% 0, rgba(0, 0, 0, 0) 70%, $primary 70%);
                transform: rotate(180deg);
            }

            &::after {
                content: '';
                position: absolute;
                width: 20px;
                height: 20px;
                left: 0;
                bottom: -27px;
                background: radial-gradient(circle at 100% 0, rgba(0, 0, 0, 0) 70%, $primary 70%);
                transform: rotate(90deg);
            }
        }

        &.editing-curve {
            &::before {
                content: '';
                position: absolute;
                width: 20px;
                height: 20px;
                right: 0;
                bottom: -20px;
                background: radial-gradient(circle at 100% 0, rgba(0, 0, 0, 0) 70%, $primary 70%);
                transform: rotate(180deg);
            }

            &::after {
                content: '';
                position: absolute;
                width: 20px;
                height: 20px;
                left: 0;
                bottom: -20px;
                background: radial-gradient(circle at 100% 0, rgba(0, 0, 0, 0) 70%, $primary 70%);
                transform: rotate(90deg);
            }
        }

        mat-icon {
            color: $background;
            opacity: 0.38;

            &.active-icon {
                color: $background;
                opacity: 1;
            }

            &.completed-icon {
                color: $accent-200;
                opacity: 1;
            }
        }

        &__tab {
            position: relative;
            border: 1px solid $background;
            color: $background;
            border-radius: 28px;
            min-width: 65px;
            height: 36px;

            &--active {
                color: $accent;
                background-color: $background;
            }

            &--completed {
                border: 1px solid $accent-200;
            }
        }

        &__actions {
            width: 100%;
            padding: 12px 16px;
            background-color: darken($bg-background, 5%);
        }
    }

    .below-tabs {
        margin-bottom: 20px;
    }
}

