(eventControls: FormArray): ValidationErrors | null => {
    let validEvents = true;
    if (!eventControls?.controls) {
        validEvents = false;
    } else {
        eventControls.controls.forEach((control) => {
            try {
                const event: Event = control.value;
                if (!(event && event.recurrence && event.startDateTime && event.endDateTime)) {
                    validEvents = false;
                } else {
                    const eventRecurrence = RRule.fromString(event.recurrence);
                    const eventStart = new Date(parseInt(event.startDateTime, 10));
                    const eventEnd = new Date(parseInt(event.endDateTime, 10));

                    if (
                        !(eventRecurrence.isFullyConvertibleToText() && eventStart && eventEnd && eventEnd > eventStart)
                    ) {
                        validEvents = false;
                    }
                }
            } catch (error) {
                return { invalidEvent: true };
            }
        });
    }

    if (validEvents) {
        return null;
    } else {
        return { invalidEvent: true };
    }
}

(eventFormGroup: FormGroup): ValidationErrors | null => {
    let validEventTime = true;
    if (!eventFormGroup?.controls) {
        validEventTime = false;
    } else {
        const startDateControl = eventFormGroup.get('startDate');
        const startDateValue = new Date(startDateControl.value);

        const endDateControl = eventFormGroup.get('endDate');
        const endDateValue = new Date(endDateControl.value);

        if (endDateValue <= startDateValue) {
            validEventTime = false;
        }
    }

    if (validEventTime) {
        return null;
    } else {
        return { invalidEventTime: true };
    }
}

